USE precept;
DROP TABLE IF EXISTS `drg`;
CREATE TABLE `drg`(
`id` int(11) NOT NULL AUTO_INCREMENT,
`ms_drg` char(4) DEFAULT NULL,
`fy_2010_post_acute` enum ('No','Yes'),
`fy_2010_special_pay` enum ('No','Yes'),
`mdc` char(4),
`type` char(10),
`title` varchar(255),
`weights` float,
`geometric_mean_los` float,
`arithmetic_mean_los` float,
PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;

